#app {
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100vh;
  background-color: $color-gray-background;

  #container {
    display: flex;
    border-radius: $border-radius-default;
    background-color: $color-white-default;
    width: 1152px;
    height: 700px;
    box-shadow: rgba(0, 0, 0, 0.35) 0px 5px 15px;

    .form-wrapper {
      padding: 16px;
      display: flex;
      flex-direction: column;
      width: 50%;

      nav {
        display: flex;
        justify-content: center;
        padding: 28px 12px 28px 6px;

        ul {
          width: 100%;
          display: flex;
          justify-content: space-between;
          list-style: none;
          align-items: center;

          .logo-wrapper {
            display: flex;
            align-items: center;

            .point-blue {
              color: $color-blue-logo;
              font-weight: bold;
            }
          }

          li {
            .circle-blue {
              height: 25px;
              width: 25px;
              border-radius: 12.5px;
              background-color: $color-blue-logo;
              margin-right: 8px;
            }

            a.logo {
              color: $color-black-default;
            }
            a {
              color: $color-gray-nav;
            }
          }
        }
      }
      .form-content {
        display: flex;
        flex-direction: column;
        justify-content: center;
        height: 100%;

        .form-header {
          // height: 120px;
          padding-left: 36px;

          .header-info {
            color: $color-gray-nav;
            text-transform: uppercase;
            font-weight: bold;
          }

          .header-title {
            font-size: 52px;
            padding: 22px 0;

            .point-blue {
              color: $color-blue-logo;
            }
          }

          .header-question {
            color: $color-gray-nav;

            button {
              border: none;
              background: none;
              color: $color-blue-logo;
              font-size: 16px;

              &:hover {
                cursor: pointer;
              }
            }
          }
        }
      }
    }

    .img-wrapper {
      position: relative;
      background-image: url('https://images.unsplash.com/photo-1439853949127-fa647821eba0?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=987&q=80');
